    {
      "id": "startProcess",
      "name": "Start Process",
      "elementType": "startEvent",
      "description": "Start of the expense reimbursement process",
      "connectedTo": [
        {
          "targetNode": "submitExpense",
          "description": "Proceed to expense submission"
        }
      ],
      "properties": null
    },
    {
      "id": "submitExpense",
      "name": "Submit Expense",
      "elementType": "userTask",
      "description": "Employee submits expense for reimbursement",
      "connectedTo": [
        {
          "targetNode": "validateExpense",
          "description": "Proceed to expense validation"
        }
      ],
      "properties": null
    },
    {
      "id": "validateExpense",
      "name": "Validate Expense",
      "elementType": "serviceTask",
      "description": "Automated validation of the submitted expense",
      "connectedTo": [
        {
          "targetNode": "decisionPoint",
          "description": "Proceed to decision point"
        }
      ],
      "properties": null
    },
    {
      "id": "decisionPoint",
      "name": "Decision Point",
      "elementType": "exclusiveGateway",
      "description": "Branching based on the result of expense validation",
      "connectedTo": [
        {
          "targetNode": "escalateIssue",
          "description": "If validation fails, escalate issue"
        },
        {
          "targetNode": "processPayment",
          "description": "If validation is successful, process payment"
        }
      ],
      "properties": null
    },
    {
      "id": "escalateIssue",
      "name": "Escalate Issue",
      "elementType": "userTask",
      "description": "Issue is escalated to the manager for manual review",
      "connectedTo": [
        {
          "targetNode": "endProcess",
          "description": "End the process"
        }
      ],
      "properties": null
    },
    {
      "id": "processPayment",
      "name": "Process Payment",
      "elementType": "serviceTask",
      "description": "Automated processing of payment",
      "connectedTo": [
        {
          "targetNode": "paymentDecision",
          "description": "Proceed to payment decision"
        }
      ],
      "properties": null
    },
    {
      "id": "paymentDecision",
      "name": "Payment Decision",
      "elementType": "exclusiveGateway",
      "description": "Branching based on the result of payment processing",
      "connectedTo": [
        {
          "targetNode": "paymentError",
          "description": "If payment processing fails, handle error"
        },
        {
          "targetNode": "endProcess",
          "description": "If payment processing is successful, end the process"
        }
      ],
      "properties": null
    },
    {
      "id": "paymentError",
      "name": "Payment Error",
      "elementType": "userTask",
      "description": "Payment error is handled by the support team",
      "connectedTo": [
        {
          "targetNode": "endProcess",
          "description": "End the process"
        }
      ],
      "properties": null
    },
    {
      "id": "endProcess",
      "name": "End Process",
      "elementType": "endEvent",
